import React, {useState} from 'react'
import axios from 'axios';


export default function Login({setIsLogin}) {
    const [user, setUser] = useState({name: '',email: '',password: ''})
    const [err, setErr] = useState('')

    const onChangeInput = e =>{
        const {name, value} = e.target;
        setUser({...user, [name]:value})
        setErr('')
    }

    const registerSubmit = async e =>{
        e.preventDefault()
        try {
             const res = await axios.post('/users/register', {
                 username: user.name,
                 email: user.email,
                 password: user.password
             })
             setUser({name: '', email: '', password: ''})
             setErr(res.data.msg)
        } catch(err) {
           err.response.data.msg && setErr( err.response.data.msg) 
        }
    }

    const loginSubmit = async e =>{
        e.preventDefault()
        try {
             const res = await axios.post('/users/login', {
                 email: user.email,
                 password: user.password
             })
             setUser({name: '', email: '', password: ''})
             localStorage.setItem('tokenStore', res.data.token)
             setIsLogin(true)
        } catch(err) {
           err.response.data.msg && setErr( err.response.data.msg) 
        }
    }

       const [onLogin, setOnLogin] = useState(false)
       const style = {
           visibility: onLogin ? "visible" : "hidden",
           opacity: onLogin ? 1 : 0,

       }

    return (
        <section className="login-page">
          <div className="login create-note">
               <h2 >iNOTE</h2>
               <form onSubmit={loginSubmit}>
                   <input type="email" name="email" id="login-email"
                   placeholder="Email" required value={user.email}
                   onChange={onChangeInput} />


                   <input type="password" name="password" id="login-password"
                   placeholder="Password" required value={user.password}
                   autoComplete="true"
                   onChange={onChangeInput} />


                   <button class="buttontxt" type="submit">LOGIN</button>
                        <div> </div>
                       <span  class="bottomtxt" onClick={() => setOnLogin(true)}> Create account</span>

                    <h3>{err}</h3>
               </form>
            </div>  
           <div className="register create-note" style={style}>
           <h2>Register</h2>
               <form onSubmit={registerSubmit}>
                   <input type="text" name="name" id="register-name"
                    placeholder="User Name" required value={user.name}
                    onChange={onChangeInput} />

                   <input type="email" name="email" id="register-email"
                   placeholder="Email" required value={user.email}
                   onChange={onChangeInput} />


                   <input type="password" name="password" id="register-password"
                   placeholder="Password" required value={user.password}
                   autoComplete="true" onChange={onChangeInput} />


                   <button class="buttontxt" type="submit">Register</button>

                       <span class="bottomtxt" onClick={() => setOnLogin(false)}> Login Now</span>

                    <h3>{err}</h3>
               </form>
            </div>
    </section>
    )
}
